INSERT INTO `ket_progress_srko` VALUES ('17000025', '138', 'KH', '3', '2017', 'Dibawah target', 'Sedikit dibawah target, ada dua proyek terkendala oleh pihak ke 3 dalam proyek (PT KOS dan KWOSM) , PT INKA tidak siapnya internal team PT INKA dan user yang akan menggunakan sistem HC.', 'Budi Badru, Andik.', 'Januari-Maret 2017', 'Unit Manufacturing', 'Efisiensi proyek', '1. Membuat rencana kerja lebih detail\r\n2. Pemetaan User Requirment yang lebih detail.\r\n3. Peningkatan kemampuan Project Manager.\r\n4. Peningkatan kemampuan SDM team SBU-Non SAP.\r\n\r\n', '2 minggu', '14 April 2017', 'Manufacturing', 'Manager', '<p>Sedikit dibawah target, ada dua proyek terkendala oleh pihak ke 3 dalam proyek (PT KOS dan KWOSM) , PT INKA tidak siapnya internal team PT INKA dan user yang akan menggunakan sistem HC.. Budi Badru, Andik.. Januari-Maret 2017. Unit Manufacturing. Efisiensi proyek. 1. Membuat rencana kerja lebih detail 2. Pemetaan User Requirment yang lebih detail. 3. Peningkatan kemampuan Project Manager. 4. Peningkatan kemampuan SDM team SBU-Non SAP. . 2 minggu. 14 April 2017. Manufacturing. Manager</p>\r\n', null, null, null);
