INSERT INTO `ket_progress_srko` VALUES ('17000067', '46', 'M4100', '4', '2017', 'Progress 0%', 'Aktual proses 100%. Progress 0% terjadi karena di GCA belum sempurna, dan juga personil tidak pernah mengisi GCA ', 'Yang melaksanakan pekerjaan ini adalah Pak Yuliarso', 'Terjadi sejak GCA dibuat', 'Berdampak pada progress Divisi HC', 'Hal ini terjadi karena Pak Yuliarso merasa bahwa sebentar lagi akan pensiun jadi tidak memerlukan lagi penilaian terhadap yang bersangkutan', 'Memberikan penjelasan lebih rinci kepada yang bersangkutan', 'Kerugian hanya pada Pak Yuliarso', 'Dilaporkan tanggal 15 Mei 2017', 'Rapat Kinerja Perusahaan', 'Direksi dan Peserta Rapat', '<p>Aktual proses 100%. Progress 0% terjadi karena di GCA belum sempurna, dan juga personil tidak pernah mengisi GCA . Yang melaksanakan pekerjaan ini adalah Pak Yuliarso. Terjadi sejak GCA dibuat. Berdampak pada progress Divisi HC. Hal ini terjadi karena Pak Yuliarso merasa bahwa sebentar lagi akan pensiun jadi tidak memerlukan lagi penilaian terhadap yang bersangkutan. Memberikan penjelasan lebih rinci kepada yang bersangkutan. Kerugian hanya pada Pak Yuliarso. Dilaporkan tanggal 15 Mei 2017. Rapat Kinerja Perusahaan. Direksi dan Peserta Rapat</p>\r\n', null, null, null);
