INSERT INTO `ket_progress_srko` VALUES ('17000095', '142', 'KH', '5', '2017', 'Belum Mendapatkan Pelanggan Baru', 'Pelanggan baru nelum ada. Seharusnya sudah ada 1 pelanggan baru', 'Yang terkait: Imam dan Budi', 'Kejadian sejak 1 Januari 2017 sampai sekarang', 'Unit Sales', 'Target Penjualan ke RSJD Bengkulu tertunda', 'Laternatifnya mencari kandidat konsumen baru', 'Keruguannya Revenue dan Produktifitas tidak tercapai', 'Dilaporkan 8 Juni 2017', 'Dilaporkan di Dash Board SSO', 'Dilkaporkan ke Manajemen', '<p>Pelanggan baru belum ada. Seharusnya sudah ada 1 pelanggan baru. Yang terkait: Imam dan Budi. Kejadian sejak 1 Januari 2017 sampai sekarang. Unit Sales. Target Penjualan ke RSJD Bengkulu tertunda. Alternatifnya mencari kandidat konsumen baru. Keruguinnya Revenue dan Produktifitas tidak tercapai. Dilaporkan 8 Juni 2017. Dilaporkan di Dash Board SSO. Dilkaporkan ke Manajemen</p>\r\n', null, null, null);
