INSERT INTO `ket_progress_srko` VALUES ('17000097', '62', 'M4300', '5', '2017', 'Pengelolaan KPKU belum 100%', 'Pelaksanaan penilaian KPKU 2016 diundur ke bulan Juli 2017 dari semula bulan Juni 2017', 'semua divisi', 'sejak 8 Mei 2017', 'semua area', 'Diundur oleh ', 'mengikuti rencana dari KS', 'mundur mendapatkan feedback report KPKU', 'kira-kira 8 Mei 2017', 'Divisi CT', 'ke Kepala Divisi Human Capital', '<p>Pelaksanaan penilaian KPKU 2016 diundur ke bulan Juli 2017 dari semula bulan Juni 2017. semua divisi. sejak 8 Mei 2017. semua area. Diundur&nbsp; mengikuti rencana dari KS. mundur mendapatkan feedback report KPKU. kira-kira 8 Mei 2017. Divisi CT. ke Kepala Divisi Human Capital</p>\r\n', null, null, null);
