INSERT INTO `ket_progress_srko` VALUES ('17000099', '110', 'KL', '5', '2017', 'Pencapaian target margin proyek', 'Pencapaian target margin proyek masih 0 dari seharusnya 18%', ' Mgr SBU', 'Sejak 1 Mei 2017 s/d 31 Mei 2017', 'SBU EI', 'Data sudah ada di Simpro (cc proyek KL21701) tetapi belum diupdate oleh Divisi Keuangan', 'Koordinasi dengan Divisi Keuangan', 'Pencapaian target margin proyek masih 0 (performance SBU kurang baik)', 'Tanggal 12 Juni 2017', 'Di R. Ruang Rapat Consulting', 'Manajemen Perusahaan', '<p>Pencapaian target margin proyek masih 0 dari seharusnya 18%. Mgr SBU. Bulan Mei 2017. SBU EI. Data sudah ada di Simpro (cc proyek KL21701) tetapi belum diupdate oleh Divisi Keuangan. Koordinasi dengan Divisi Keuangan. Pencapaian target margin proyek masih 0 (performance SBU kurang baik). dilaporkan pada Tanggal 12 Juni 2017. Di R. Ruang Rapat Consulting. Manajemen Perusahaan</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000099', '113', 'KL', '5', '2017', 'Pencapaian pelanggan repeat order', 'Pencapaian target pelanggan repeat order utk bulan ini 0 dari seharusnya 1', 'Mgr SBU', 'Sejak 1 Mei 2017 s/d 31 Mei 2017', 'SBU EI', 'Prospek dari KPDP masih tertunda', 'Lebih intensif mengejar prospek di KPDP', 'Pencapaian target pelanggan repeat order masih belum terpenuhi', 'Akan dilaporkan pada Tanggal 12 Juni 2017 ', 'Di R. Ruang Rapat Consulting', 'Manajemen Perusahaan', '<p>Pencapaian target pelanggan repeat order utk bulan ini 0 dari seharusnya 1. Mgr SBU. Sejak 1 Mei 2017 s/d 31 Mei 2017. SBU EI. Prospek dari KPDP masih tertunda. Lebih intensif mengejar prospek di KPDP. Pencapaian target pelanggan repeat order masih belum terpenuhi. Akan dilaporkan pada Tanggal 12 Juni 2017. Di R. Ruang Rapat Consulting. Manajemen Perusahaan</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000099', '128', 'KB', '5', '2017', 'Meraih pelanggan baru tidak tercapai', 'Target pelanggan baru bulan mei adalah KBS akan tetapi sampai saat ini belum terealisasi  dikarenakan banyaknya procedure di KBS', 'Diane', 'sejak bulan Mei 2017', 'Yang terkena dampak SBU SAP', 'Prosedure pengadaan yang terlalu panjang mulai dari penyusunan TOR,FS,budgeting, Pengkajian migrasi dr system lama ke system baru dll', 'Mempercepat syarat2 yang harus kita dipenuhi dengan dukungan dari management ', 'kerugian 7M', '15 Juni 2017', 'Rapat Kinerja', 'Direksi dan peserta rapat', '<p>Target pelanggan baru (KBS) tidak tercapai, Yang terkait dalam hal ini SBU SAP. terjadi bulan Mei 2017. Yang terkena dampak SBU SAP. Karena prosedure pengadaan di KBS yang terlalu panjang mulai dari penyusunan TOR,FS,budgeting, pengkajian migrasi dr system lama ke system baru dll. Cara menanganinya dengan mempercepat syarat2 yang harus kita dipenuhi dengan dukungan dari management . kerugian target pendapatan dari KBS sebesr 7M tidak tercapai. dilaporkan tgl 12 Juni 2017.di&nbsp; Rapat Kinerja. Direksi dan peserta rapat</p>\r\n', null, null, null);
