INSERT INTO `ket_progress_srko` VALUES ('17000101', '152', 'KJ', '5', '2017', 'Pendapatan: \r\n<br><b>Proyek Layananan Mtc MES [MS2]</b> \r\n<br>Bulan Oktober 2016 (Blm ada kepastian), \r\n<br>Bulan Januari 2017  Sudah dibayar\r\n<br>Bulan Februari 2017  Masih Direvisi Pak Helmi\r\n<br>Bulan Maret 2017   Masih Direvisi Pak Helmi\r\n<br>Bulan April 2017  Masih Direview Bu Weni\r\n<br>Bulan Mei 2017   proses pembuatan BA\r\n<br><b>LHP Proyek SCADA 3 KTI - BA</b> \r\n<br> Jasa implementasi masih belum di ttd direksi KTI, \r\n<br>Pendapatan Training &Dokumentasi belum bisa diproses KTI. \r\n<br><b>Proyek Truck Scale </b>\r\n<br> BA Construction Borring Pile siap dan akan diproses (Rp 485 jt) \r\n<br><b>Proyek Tolling </b>\r\n<br> Proses penagihan  Advance Payment\r\n<br> Proses penagihan Milestone bisnis blueprint\r\n<br><b>Proyek Mobile inspection </b>\r\n<br> Status penagihan  Milestone : belum ada', '<br> - untuk MES2/MES bulan Oktober 2016 masih terkendala adanya sarang dari SPI KS untuk tidak membayar dikarenakan overlaping dengan FAC project implementasi MES\r\n<br> - Penagihan uang muka project tolling terkendala sertifikat uang muka belum di ttd Pak Cahyo', 'Cyndi', '-', '-', '- Proses review di Pak Helmi butuh waktu sangat lama karena ybs sangat sibuk menangani banyak project', '-', '-', '-', '-', '-', '<ul>\r\n	<li>Proses review BA di Pak Helmi butuh waktu sangat lama karena ybs sangat sibuk menangani banyak project</li>\r\n</ul>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000102', '125', 'KB', '5', '2017', 'Meraih Pendapatan Proyek tidak tercapai', 'Dikarenakan adanya pengunduran proyek e-cataloging yang seharusnya di akhir tahun 2016 proses bidding ulang pada bulan Mei 2017 sehingga prospek SAP menjadi mundur', 'Vincent', 'Mei 2017', 'SBU SAP', 'Karena proyek e-cataloging mundur', 'Dengan mencari proyek lain seperti body shopping, CR MS2 KNSS ', 'Pendapatan dari petronas sebesar 900juta tidak terjadi ', 'ini tgl 12 juni 2017', 'di Rapat Kinerja', 'Direksi dan peserta rapat', '<p>Target pendapatan proyek 2,3M tidak tercapai. yang terkait SBU SAP. terjadi Mei 2017. yang terkena dampak SBU SAP dan Keuangan. Hal ini terjadi karena adanya pengunduran proyek e-cataloging yang seharusnya di akhir tahun 2016, proses bidding diulang pada bulan Mei 2017. Dengan mencari proyek lain seperti body shopping dan CR MS2 KNSS . Kerugiannya pendapatan dari Petronas sebesar 900juta tidak terjadi. dilaporkan tgl 12 juni 2017. di Rapat Kinerja. Direksi dan peserta rapat</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000103', '126', 'KB', '5', '2017', 'Meraih Kontrak Baru tidak mencapai target', 'Dikarenakan proyek dari Petronas belum terjadi sebesar 3 M', 'Vincent', 'Mei 2017', 'SBU SAP dan Keuangan', 'Karena proyek e-cataloging di Petronas mundur mengakibatkan Proyek SAP menjadi mundur juga', 'Dengan mencari proyek lain seperti body shopping, CR MS2 KNSS ', 'Kontrak dari Petronas sebesar 3M tidak terealisasi ', 'tgl 12 juni 2017', 'di Rapat Kinerja.', 'Direksi dan peserta rapat', '<p>Target proyek Petronas sebesar 3 M tidak tercapai. yang terkait SBU SAP. Terjadi mei 2017. yang terkena dampak SBU SAP dan Keuangan. Karena proyek e-cataloging di Petronas mundur mengakibatkan proyek SAP menjadi mundur juga. Cara menghindari agar tidak lebih buruk dengan mencari proyek lain seperti body shopping, CR MS2 KNSS . Kerugiannya kontrak dari Petronas sebesar 3M tidak terealisasi . dilaporkan tgl 12 juni 2017. di Rapat Kinerja. Direksi dan peserta rapat</p>\r\n', null, null, null);
