INSERT INTO `ket_progress_srko` VALUES ('17000114', '117', 'KL', '5', '2017', 'Melaksanakan Penyelesaian Sales', 'Pencapaian target penyelesaian kegiatan sales masih 24.12 %  dari 100%', 'SBU-E&I', '1 Mei 2017 s/d 31 Mei 2017. ', 'Semua personil SBU EI', 'Tidak semua personil mengisi progress GCA dan pengisian tidak mengikuti rencana di GCA. ', 'Menambahkan program/rencana kerja sesuai peluang/prospek yg sedang ditangani. ', 'Pencapaian progress masih 24.12 %, tidak tercapai target  penyelesaian sales', 'Akan dilaporkan pada pertengahan bulan Juni 2017. ', 'Dalam rapat Review Kinerja & SKK. ', 'Direksi', '<p>Pencapaian target penyelesaian kegiatan sales masih 24.12 % dari 100%. SBU-E&amp;I. 1 Mei 2017 s/d 31 Mei 2017. . Semua personil SBU EI. Tidak semua personil mengisi progress GCA dan pengisian tidak mengikuti rencana di GCA. . Menambahkan program/rencana kerja sesuai peluang/prospek yg sedang ditangani. . Pencapaian progress masih 24.12 %, tidak tercapai target penyelesaian sales. Akan dilaporkan pada pertengahan bulan Juni 2017. . Dalam rapat Review Kinerja &amp; SKK. . Direksi</p>\r\n', null, null, null);
