INSERT INTO `ket_progress_srko` VALUES ('17000120', '48', 'M4100', '5', '2017', 'Progress 52,62%', 'Pengelolaan Tenaga Kerja Kontrak tidak selalu terjadi setiap bulan', 'Personil yang melakukan Bu Ida', 'Terjadi sejak berlakunya GCA 2017', 'Hal ini berdampak pada kecilnya progress Pengelolaan tenaga kerja kontrak di Divisi HC', 'Penerimaan atau resign karyawan kontrak tidak selalu ada setiap bulan sementara targetnya setiap bulan harus ada', 'Harus didiskusikan lagi dengan CT untuk pekerjaan yang tidak rutin dan tidak dapat diprediksi waktunya', 'Kerugian hanya pada progress pekerjaan di Divisi HC', 'Dilaporka tanggal 12 Juni 2017', 'Pada rapat kinerja bulanan Mei 2017', 'Ke direksi dan peserta rapat', '<p>Pengelolaan Tenaga Kerja Kontrak tidak selalu terjadi setiap bulan. Personil yang melakukan pekerjaan ini adalah Bu Ida. Hal ini berdampak pada kecilnya progress Pengelolaan tenaga kerja kontrak di Divisi HC. Penerimaan atau resign karyawan kontrak tidak selalu ada setiap bulan sementara targetnya setiap bulan harus ada. Untuk mengatasinya perlu didiskusikan lagi dengan CT untuk pekerjaan yang tidak rutin dan tidak dapat diprediksi waktunya. Kerugian hanya pada progress pekerjaan di Divisi HC dilaporka tanggal 12 Juni 2017 pada rapat kinerja bulanan Mei 2017 ke direksi dan peserta rapat.</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000121', '51', 'M4100', '5', '2017', 'Progress 2,02%', 'Banyak data yang tidak diinput oleh PIC nya', 'Pelatihan dikoordinir oleh Bu Dian', 'Sejak GCA 2017 diberlakukan', 'Berdampak kepada semua unit kerja', 'Untuk pelatihan eksternal anggaran ditiadakan, sementara pelatihan internal saat ini diserahkan kepada masing-masing unit kerja tetapi tidak diinput di GCA', 'Masing-masing ketua unit kerja mengisi pelatihan internalnya ', 'Kerugian berupa minimnya kesempatan untuk mengembangkan keahlian dari karyawan', 'Dilaporkan tanggal 12 Juni 2017', 'pada rapat kinerja Mei 2017', 'ke Direksi dan peserta rapat', '<p>Banyak data yang tidak diinput oleh PIC nya di GCA. Pelatihan dikoordinir oleh Bu Dian. Hal ini berdampak kepada semua unit kerja. Sejak adanya GCA 2017 untuk pelatihan eksternal anggaran ditiadakan, sementara pelatihan internal saat ini diserahkan kepada masing-masing unit kerja tetapi tidak diinput di GCA. Untuk progress pencapaian targetnya diminta masing-masing ketua unit kerja mengisi pelatihan internalnya . Kerugian berupa minimnya kesempatan untuk mengembangkan keahlian dari karyawan. Dilaporkan tanggal 12 Juni 2017 pada rapat kinerja Mei 2017 ke Direksi dan peserta rapat.</p>\r\n', null, null, null);
