INSERT INTO `ket_progress_srko` VALUES ('17000126', '119', 'KL', '5', '2017', 'Pencapaian target program kerja tambahan ', 'Pencapaian target program kerja tambahan masih 75.17% dari seharusnya 100%', 'Manager SBU', 'Sejak 1 Mei 2017 s/d 31 Mei 2017', 'SBU EI', 'Salah memasukkan data progress pencapaian di SSO dan ada perubahan penanggung jawab program kerja', 'Revisi program kerja', 'Pencapaian progress tidak 100%', 'Tanggal 12 Juni 2017', 'R. Ruang Rapat Consulting', 'Manajemen Perusahaan', '<p>Pencapaian target program kerja tambahan masih 75.17% dari seharusnya 100%. Manager SBU. Sejak 1 Mei 2017 s/d 31 Mei 2017. SBU EI. Salah memasukkan data progress pencapaian di SSO dan ada perubahan penanggung jawab program kerja. Revisi program kerja. Pencapaian progress tidak 100%. Tanggal 12 Juni 2017. R. Ruang Rapat Consulting. Manajemen Perusahaan</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000127', '122', 'KL', '5', '2017', 'Pencapaian target jam pelatihan', 'Pencapaian target jam pelatihan masih 0 jam dari seharusnya 6 jam', 'Semua personil SBU EI', 'Sejak 1 Mei 2017 s/d 31 Mei 2017', 'SBU EI', 'Semua personil konsentrasi mengejar prospek', 'Penyusunan target pelatihan agar disesuaikan dengan aktifitas SBU pada bulan berjalan', 'Pencapaian target pelatihan tertinggal 6 jam', 'Tanggal 12 Juni 2017', 'R. Ruang Rapat Consulting', 'Manajemen Perusahaan', '<p>Pencapaian target jam pelatihan masih 0 jam dari seharusnya 6 jam. Semua personil SBU EI. Sejak 1 Mei 2017 s/d 31 Mei 2017. SBU EI. Semua personil konsentrasi mengejar prospek. Penyusunan target pelatihan agar disesuaikan dengan aktifitas SBU pada bulan berjalan. Pencapaian target pelatihan tertinggal 6 jam. Tanggal 12 Juni 2017. R. Ruang Rapat Consulting. Manajemen Perusahaan</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000128', '49', 'M4100', '5', '2017', 'Progress 45,60%', 'Tidak semua item di Program Kerja Tambahan selalu terjadi setiap bulan', 'Seluruh personil Divisi HC', 'Terjadi sejak berlakunya GCA 2017', 'Hal ini berdampak pada kecilnya progress Program Kerja Tambahan di Divisi HC', 'Ada item-item di Program Kerja Tambahan tidak selalu ada setiap bulan sementara targetnya setiap bulan harus ada', 'Harus didiskusikan lagi dengan CT untuk pekerjaan yang tidak rutin dan tidak dapat diprediksi waktunya', 'Kerugian hanya pada progress pekerjaan di Divisi HC', 'Dilaporka tanggal 12 Juni 2017', 'Pada rapat kinerja bulanan Mei 2017', 'Ke direksi dan peserta rapat', '<p>Tidak semua item di Program Kerja Tambahan terjadi setiap bulan. Seluruh personil Divisi HC melaksanakan program tambahan ini. Hal ini berdampak pada kecilnya progress Program Kerja Tambahan di Divisi HC. Ada item-item di Program Kerja Tambahan tidak selalu ada setiap bulan seperti Mengurus Data Pensiun, Klaim Obat, COB BPJS, sementara targetnya setiap bulan harus ada. Untuk mengatasinya harus didiskusikan lagi dengan CT untuk pekerjaan yang tidak rutin dan tidak dapat diprediksi waktunya. Kerugian hanya pada progress pekerjaan di Divisi HC. Dilaporkan tanggal 12 Juni 2017 pada rapat kinerja bulanan Mei 2017 ke direksi dan peserta rapat.</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000129', '135', 'KB', '5', '2017', 'Target Produktifitas tidak tercapai', 'Produktifitas bulan mei tidak tercapai karena target perolehan projek Petronas tidak tercapai', 'Team SBU SAP', 'Mei 2017', 'Yang berdampak semua unit', 'Karena proyek e-cataloging di Petronas mundur mengakibatkan proyek SAP menjadi mundur juga', 'Mengejar target penjualan lain seperti KBS,body shopping dll', 'Kerugian yang terjadi sesuai dengan nilai kontrak 3M', 'Dilaporkan tgl 15 Mei 2017', 'Rapat Kinerja', 'BOD dan peserta rapat', '<p>Produktifitas bulan mei tidak tercapai karena target perolehan projek Petronas tidak tercapai. Team SBU SAP. Sejak mei 2017. Yang terkena dampak semua unit. Karena proyek e-cataloging di Petronas mundur mengakibatkan proyek SAP menjadi mundur juga. Mengejar target penjualan lain seperti KBS,body shopping dll. Kerugian yang terjadi sesuai dengan nilai kontrak 3M. Dilaporkan tgl 12 Mei 2017. Rapat Kinerja. BOD dan peserta rapat</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000130', '136', 'KB', '5', '2017', 'Target Jam Pelatihan karyawan ', 'Kendala resources yang telah banyak mendapatkan training banyak yang resign', '. Yang terkait SBU SAP dan HC.', 'Terjadi sejak  01 April 2017', 'Yang terkena dampak SBU SAP', 'Hal ini terjadi karena masih dalam tahap evaluai resouces yang akan di training ', 'Melakukan training kembali', 'Kerugiannya banyak karena Krakatau IT menjadi kehilangan peluang untuk mendapatkan pendapatan dari resources yang sudah di training', 'Dilaporkan tgl 12 Mei 2017. ', 'Rapat Kinerja', 'BOD dan peserta rapat', '<p>Kendala resources yang telah banyak mendapatkan training resign. . Yang terkait SBU SAP dan HC.. Terjadi sejak 01 April 2017. Yang terkena dampak SBU SAP. Hal ini terjadi karena masih dalam tahap evaluai resouces yang akan di training . Mitigasi melakukan training kembali. Kerugiannya banyak, karena Krakatau IT menjadi kehilangan peluang untuk mendapatkan pendapatan dari resources yang sudah di training. Dilaporkan tgl 12 Mei 2017. . Rapat Kinerja. BOD dan peserta rapat</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000131', '158', 'KJ', '5', '2017', '<b>Sales</b>: \r\n<br>1. Proyek Kontrak baru MS2 Baru (3 Tahun) â†’ Proses Pembuatan TOR & EE di Divisi BIM KS\r\n<br>2. Perolehan kontrak Inovasi - Aplikasi Inspection Utulity CRM â†’ sudah mendapat SPK, menunggu kontrak\r\n<br>3. Prospek HSM 2 â†’ Proses Pembuatan TOR & EE di Project HSM#2\r\n<br>4. Prospek Maintenance interface  KNSS', '<br><b>INFORMASI & KENDALA</b>\r\n<br><b>MS2</b>\r\n<br>âœ… Kontrak Existing MS2 berakhir 1 Juni, saat ini diperpanjang (SPK) selama 3 bulan s/d Agustus 2017\r\n<br>âœ… Sesuai instruksi BOD KS untuk menekan fix cost KS, divisi BIM menginginkan kontrak MS2 secara komersial dalam Fix dan Variable\r\n<br>âœ… Pendekatan perhitungan KS dengan Resource based (bukan scope based), dengan perhitungan tersebut ada resiko penurunan nilai kontrak mengingat banyak resource yang keluar\r\n<br><b>HSM2</b>\r\n<br>âœ… Proses administrasi pengadaan jasa diperkirakan melebihi target kick off project agustus 2017\r\n<br>âœ… Scoope meliputi MES, SAP dan infrastruktur, penawaran resmi MES sudah dikirimkan\r\n<br>âœ… Sudah dilakukan Event joint planning session\r\n<br>âœ… Update 12.06.2017, Dokumen ToR sudah di perencanaan\r\n<br><b>Maintenance Interface KNSS</b>\r\n<br>âœ… Penawaran resmi sudah di kirimkan\r\n<br>âœ… Tim KNSS (Kojima) tidak menginginkan proyek Maintenance Services inteface KNSS, namun setuju untuk Kontrak Maintenace License (ATS)\r\n<br><b>Mobile Inspection CRM</b>\r\n<br>âœ… SPK sudah dibuat\r\n<br>âœ… Proses Kontrak', '<br><b>WHO</b>\r\n<br> sales Maintenance MES : fatah\r\n<br> sales Prospek HSM#2 : fatah + Feriano\r\n<br> sales Prospek Mtc iface KNSS : Djoko + Wahyudin\r\n<br> sales Mobile Inspection : Ari ', '-', '<br><b>WHERE</b> Area terdampak\r\n<br> Mtc MES (MS2), SBU otomasi & SBU SAP\r\n<br> Prospek HSM#2, SBU otomasi, SBU SAP, SBU Infrastruktur\r\n', '.', '.', '.', '<br><b>TIME</b>\r\nRapat Kinerja', '.', '<br><b> ORGANIZATION<.b>\r\nDireksi ', '<p><br />\r\n<strong>INFORMASI &amp; KENDALA</strong><br />\r\n<strong>MS2</strong><br />\r\nâœ… Kontrak Existing MS2 berakhir 1 Juni, saat ini diperpanjang (SPK) selama 3 bulan s/d Agustus 2017<br />\r\nâœ… Sesuai instruksi BOD KS untuk menekan fix cost KS, divisi BIM menginginkan kontrak MS2 secara komersial dalam Fix dan Variable<br />\r\nâœ… Pendekatan perhitungan KS dengan Resource based (bukan scope based), dengan perhitungan tersebut ada resiko penurunan nilai kontrak mengingat banyak resource yang keluar<br />\r\n<strong>HSM2</strong><br />\r\nâœ… Proses administrasi pengadaan jasa diperkirakan melebihi target kick off project agustus 2017<br />\r\nâœ… Scoope meliputi MES, SAP dan infrastruktur, penawaran resmi MES sudah dikirimkan<br />\r\nâœ… Sudah dilakukan Event joint planning session<br />\r\n<strong>Maintenance Interface KNSS</strong><br />\r\nâœ… Penawaran resmi sudah di kirimkan<br />\r\nâœ… Tim KNSS (Kojima) tidak menginginkan proyek Maintenance Services inteface KNSS, namun setuju untuk Kontrak Maintenace License (ATS)<br />\r\n<strong>Mobile Inspection CRM</strong><br />\r\nâœ… SPK sudah dibuat<br />\r\nâœ… Proses Kontrak.<br />\r\n<strong>WHO</strong><br />\r\nsales Maintenance MES : fatah<br />\r\nsales Prospek HSM#2 : fatah + Feriano<br />\r\nsales Prospek Mtc iface KNSS : Djoko + Wahyudin<br />\r\nsales Mobile Inspection : Ari . -.<br />\r\n<strong>WHERE</strong> Area terdampak<br />\r\nMtc MES (MS2), SBU otomasi &amp; SBU SAP<br />\r\nProspek HSM#2, SBU otomasi, SBU SAP, SBU Infrastruktur . .. .. ..<br />\r\n<strong>TIME</strong> Rapat Kinerja. ..<br />\r\n&nbsp;</p>\r\n\r\n<p><strong>ORGANIZATION</strong></p>\r\n\r\n<p><strong>Direksi </strong></p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000132', '165', 'KD', '5', '2017', 'Meraih PendÃ patan Proyek', 'Pendapatan tidak mencapai target bulan ini', 'SBU Infrastruktur khuusnya pendapatan milestone-5 KNSS', 'Bulan Mei 2017', 'Berdampak pada SBU Infrastruktur', 'Pendapatan sudah diakui pada bulan-bulan sebelumnya', 'Tidak ada ', 'Tidak ada kerugian financial', 'Pada 12 Mei 2017', 'Rapat Kinerja Bulanan', 'Dihadiri oleh BOD dan peserta rapat', '<p>Pendapatan bulana Mei tidak memenuhi target.Walaupun Pendapatan telah diperoleh pada bulan Mei. Berdampak pada perolehan pendapatan SBU Infrastruktur. Pengakuan Pendapatan telah dilakukan ada bulan-bulan sebelumnya oleh Keuangan. Seharusnya pendapatan diakui pada bulan berjalan, dilaporkan Pada 12 Mei 2017 pada Rapat Kinerja Bulanan. Dihadiri oleh BOD dan peserta rapat</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000133', '166', 'KD', '5', '2017', 'Meraih Kontrak Baru', 'Kontrak baru bulan Mei tidak mencapai target', 'Account Manager SBU infrastruktur', 'Mei 2017', 'SBU Infrastruktur', 'Kontrak yang seharusnya ditargetkan diperoleh pada bulan Mei 2017, sudah diperoleh pada bulan sebelumnya', 'Jadi tidak ada kendala ', 'Dan tidak ada kerugian yang terjadi', 'Dilaporkan pada 12 Mei 2017', 'Pada Rapat Review Kinerja bulanan', 'Yang dihadiri oleh BOD dan peserta rapat', '<p>Kontrak baru bulan Mei tidak mencapai target. Pendapatan dicreate dan dilakukan oleh Account Manager SBU infrastruktur. Untuk Mei 2017 Berdampak pada SBU Infrastruktur. Kontrak yang seharusnya ditargetkan diperoleh pada bulan Mei 2017, sudah diperoleh pada bulan sebelumnya. Jadi tidak ada kendala . Dan tidak ada kerugian yang terjadi. Dilaporkan pada 12 Mei 2017. Pada Rapat Review Kinerja bulanan. Yang dihadiri oleh BOD dan peserta rapat</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000134', '168', 'KD', '5', '2017', 'Meraih Pelanggan Baru', 'Belum mendapat Pelanggan Baru', 'SBU Infruktur', 'Target mendapat pelanggan baru pada Mei belum tercapai', 'Berdampak pada SBU Infrastruktur', 'Prospek yang sudah kita create sampai saat ini belum mendapat respon dari customer yang meminta penawaran ke kita', 'Account Manager dengan jntens menanyakan kelanjutan dari prospek tersebut kepada customer', 'Belum terjadi kerugian financial, hanya target perolehan Pelanggan baru belum terpenuhi', 'Dilaporkan pada Rapat Review Kinerja Bulanan', 'Pada 12 Mei 2017', 'Yang dihadiri oleh BOD dan peserta rapat', '<p>Belum mendapat Pelanggan Baru. SBU Infruktur. Target mendapat pelanggan baru pada Mei belum tercapai. Berdampak pada SBU Infrastruktur. Prospek yang sudah kita create sampai saat ini belum mendapat respon dari customer yang meminta penawaran ke kita. Account Manager dengan secara intens menanyakan kelanjutan dari prospek tersebut kepada customer. Belum terjadi kerugian financial, hanya target perolehan Pelanggan baru belum terpenuhi. Dilaporkan pada Rapat Review Kinerja Bulanan. Pada 12 Mei 2017. Yang dihadiri oleh BOD dan peserta rapat</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000135', '169', 'KD', '5', '2017', 'Penyeselaian Proyek Sesuai Skedul', 'Terjadi keterlambatan Penyelesaian Proyek sesuai skedul yang sebenarnya tidak terjadi secara aktual dilapangan', 'PM proyek tidak rajin membuat laporan yang harus dimasukkan di SSO', 'Terjadi Sejak Januari 2017', 'Berdampak pada SBU Infrastruktur ', 'Terjadi karena tidak secara rajin melaporkan kegiatan ke SSO', 'Dapat diselesaikan dengan memberikan laporan berkala kedalam SSO dan dkmonitor secara ketat ', 'Tidak ada kerugian financial yang terjadi', 'Dilaporkan pada Rapat Review Kinerja', 'Pada bulan Mei 2017', 'Yang dihadiri oleh BOD dan peserta rapat', '<p>Terjadi keterlambatan Penyelesaian Proyek sesuai skedul yang sebenarnya tidak terjadi secara aktual dilapangan. PM proyek tidak rajin membuat laporan yang harus dimasukkan di SSO. Terjadi Sejak Januari 2017. Berdampak pada SBU Infrastruktur . Terjadi karena tidak secara rajin melaporkan kegiatan ke SSO. Dapat diselesaikan dengan memberikan laporan berkala kedalam SSO dan dkmonitor secara ketat . Tidak ada kerugian financial yang terjadi. Dilaporkan pada Rapat Review Kinerja. Pada bulan Mei 2017. Yang dihadiri oleh BOD dan peserta rapat</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000136', '171', 'KD', '5', '2017', 'Target melaksanakan penyelesaian Sales', 'Target penyelesaian sales hanya tercapai 13%', 'Para Account Manager', 'Sejak Januari 2017', 'Berdampak pada penyelesaian sales SBU Infrastruktur', 'Hal ini terjadi karena para Account Manager tidak secara rutin dan rajin memesukkan dan melaporkan kegiatan-kegiatan yang dilakukan kedalam SSO', 'Untuk itu para AM harus rajin memasukkan semua kegiatan yang dilakukan kedalam SSo', 'Tidak ada kerugian financial yang terjadi', 'Dilaporkan pada Rapat Review Kinerja ', 'Bulan Mei 2017', 'Yang dihadiri oleh BOD dan peserta rapat', '<p>Target penyelesaian sales hanya tercapai 13%. Para Account Manager. Sejak Januari 2017. Berdampak pada penyelesaian sales SBU Infrastruktur. Hal ini terjadi karena para Account Manager tidak secara rutin dan rajin memesukkan dan melaporkan kegiatan-kegiatan yang dilakukan kedalam SSO. Untuk itu para AM harus rajin memasukkan semua kegiatan yang dilakukan kedalam SSO. Tidak ada kerugian financial yang terjadi. Dilaporkan pada Rapat Review Kinerja . Bulan Mei 2017. Yang dihadiri oleh BOD dan peserta rapat</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000137', '173', 'KD', '5', '2017', 'Program Kerja Tambahan', 'Target program tambahan hanya tercapai 12,5%', 'Kepala Divisi Infrastruktur', 'Terjadi sejak Januari 2017', 'Area terdapak SBU Infrastruktur', 'Beberapa program belum berjalan sesuai target waktu yang ditetapkan ', 'Akan dilakukan evaluasi program dan penyelesaian yang terbaik untuk menyelesaikan program-program yang masih terlambat', 'Tidak ada kerugian financial', 'Dilaporkan pada Rapat Review Kinerja ', 'Bulan Mei 2017', 'Yang dihadiri oleh BOD dan peserta rapat', '<p>Target program tambahan hanya tercapai 12,5%. Kepala Divisi Infrastruktur. Terjadi sejak Januari 2017. Area terdampak SBU Infrastruktur. Beberapa program belum berjalan sesuai target waktu yang ditetapkan . Akan dilakukan evaluasi program dan penyelesaian yang terbaik untuk menyelesaikan program-program yang masih terlambat. Tidak ada kerugian financial. Dilaporkan pada Rapat Review Kinerja . Bulan Mei 2017. Yang dihadiri oleh BOD dan peserta rapat</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000138', '175', 'KD', '5', '2017', 'Menerapkan Program SK EDPC', 'Target program ini samapai saat ini baru terealisasi 22%', 'Ketua EDPC internal', 'Sejak Januari 2017', 'Internal Perusahaan terdampak\r\n', 'Sinkronisasi lintas fungsi dan pelaporan kegiatan ke dalam SSO', 'Diselesaiakan sinkronisasi lintas fungsi dan ketua EDPC memasukkan kegiatan-kegiatan yang dilakukan ke dalam SSO', 'Tidak ada kerugian financial yang terjagi hanya berdampak pada program tidak berjalan sesuai target', 'Dilaporkan pada 12 Mei 2017', 'Pada Rapat Review Kinerja Bulanan', 'Yang dihadiri oleh BOD dan peserta rapat', '<p>Target program ini samapai saat ini baru terealisasi 22%. Ketua EDPC internal. Sejak Januari 2017. Internal Perusahaan terdampak . Sinkronisasi lintas fungsi dan pelaporan kegiatan ke dalam SSO. Diselesaiakan sinkronisasi lintas fungsi dan ketua EDPC memasukkan kegiatan-kegiatan yang dilakukan ke dalam SSO. Tidak ada kerugian financial yang terjagi hanya berdampak pada program tidak berjalan sesuai target. Dilaporkan pada 12 Mei 2017. Pada Rapat Review Kinerja Bulanan. Yang dihadiri oleh BOD dan peserta rapat</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000139', '176', 'KD', '5', '2017', 'Meningkatkan produktivitas', 'Target tercapai sebesar 88% secara kululatif', 'Manager SBU Infrastruktur', 'Sejak Mei 2017', 'Berdampak pada SBU Infrastruktur dan perusahaan', 'Pendapatan yang diperoleh pada bulan ini kurang ', 'Pendapatan sudah deperoleh pada bulan-bulan sebelumnya', 'Tidak ada kerugian', 'Dilaporkan pada 12 Mei 2017', 'Dalam Rapat Review Kinerja Bulanan', 'Yang dihadiri oleh BOD dan Peserta rapat', '<p>Target tercapai sebesar 88%. Manager SBU Infrastruktur. Sejak Mei 2017. Berdampak pada SBU Infrastruktur dan perusahaan. Pendapatan yang diperoleh kurang walaupun target pendapatan sampai Mei 2017 tercapai 104%. Diatasi dengan mmencari tambahan pendapatan pada belan berikutnya sebesar 100 juta perbulan. Tidak ada kerugian. Dilaporkan pada 12 Mei 2017. Dalam Rapat Review Kinerja Bulanan. Yang dihadiri oleh BOD dan Peserta rapat</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000140', '177', 'KD', '5', '2017', 'Target Jam Pelatihan per Karyawan', 'Target bRu tercapai 46%', 'SBU infrastruktur secara keseluruhan', 'Terjadi sejak  Januari 2017', 'Berdampak pada SBU Infrastruktur', 'Waktu pelaksanaan dan ketersediaan karyawan untuk pelatihan banyak dihabiskan untuk melaksanakan dan menyelesaikan proyek berjalan', 'Diatasi dengan meluangkan waktu untuk melakukan pelatihan mandiri', 'Tidak ada kerugian financial hanya kerugian pada masing- masing karyawan akan kurang mendapatkan update teknologi', 'Dilaporkan pada 12 Mei 2017', 'Pada Rapat Rebiew Kinerja Bulanan', 'Yang dihadiri oleh BOD dan peserta rapat', '<p>Target bRu tercapai 46%. SBU infrastruktur secara keseluruhan. Terjadi sejak Januari 2017. Berdampak pada SBU Infrastruktur. Waktu pelaksanaan dan ketersediaan karyawan untuk pelatihan banyak dihabiskan untuk melaksanakan dan menyelesaikan proyek berjalan. Diatasi dengan meluangkan waktu untuk melakukan pelatihan mandiri. Tidak ada kerugian financial hanya kerugian pada masing- masing karyawan akan kurang mendapatkan update teknologi. Dilaporkan pada 12 Mei 2017. Pada Rapat Rebiew Kinerja Bulanan. Yang dihadiri oleh BOD dan peserta rapat</p>\r\n', null, null, null);
