INSERT INTO `ket_progress_srko` VALUES ('17000152', '46', 'M4100', '6', '2017', 'Progress 0%', 'Monitoring Biaya ATK, Air dan Listrik tidak pernah diinput', 'oleh Pak Yuliarso', 'Sejak diberlakukannya GCA 2017', 'Berdampak kepada Laporan Kinerja Divisi HC', 'Personil ybs merasa sudah tidak perlu mengisi karena akan pensiun', 'Mendorong ybs untuk mengisi', 'Kerugian ada pada Laporan Kinerja Divisi HC', 'Dilaporkan tanggal 12 Juni 2017', 'Pada rapat kinerja Mei 2017', 'ke Direksi dan peserta rapat', '<p>Monitoring Biaya ATK, Air dan Listrik tidak pernah diinput oleh Pak Yuliarso. Hal ini terjadi sejak diberlakukannya GCA 2017. Berdampak kepada Laporan Kinerja Divisi HC. Personil ybs merasa sudah tidak perlu mengisi karena akan pensiun per 1 Agustus 2017, penggantinya (Pak Bakoh) sudah mulai diajarkan cara mengisi. Kerugian ada pada Laporan Kinerja Divisi HC. Dilaporkan tanggal 26 Juli 2017. Pada rapat kinerja Juni 2017 ke Direksi dan peserta rapat.</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000153', '53', 'M4100', '6', '2017', 'Progress 0%', 'Pada bulan ini tidak ada personil yang melakukan pembelajaran', 'Terkait kepada seluruh personil Divisi HC', 'Kejadian bulan Mei 2017', 'Berdampak pada area Divisi HC karena tidak mendapatkan ilmu tambahan', 'Hal ini terjadi karena belum adanya jadwal pembelajaran untuk personil HC', 'Dibuatkan jadwal masing-masing personil paling tidak untuk pembelajaran mandiri', 'Kerugian pada laporan kinerja Divisi HC', 'Dilaporkan tanggal 12 Juni 2017', 'pada rapat kinerja Mei 2017', 'ke Direksi dan peserta rapat', '<p>Pada bulan Juni 2017 tidak ada personil HC yang melakukan pembelajaran. Dampaknya pada area Divisi HC karena tidak mendapatkan ilmu tambahan. Hal ini terjadi karena belum adanya jadwal pembelajaran untuk personil HC dan bulan puasa &amp; libur bersama lebaran. Untuk ke depannya masing-masing personil HC diminta untuk pembelajaran mandiri. Kerugian ada pada laporan kinerja Divisi HC. Dilaporkan tanggal 26 Juli 2017 pada rapat kinerja Juni 2017 ke Direksi dan peserta rapat.</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000154', '52', 'M4100', '6', '2017', '<p>Progress 87,10%</p>\r\n', 'Banyak data yang belum dapat dimasukkan ke aplikasi HCIS', 'Personil yang terkait adalah Bu Tetty', 'Terjadi sejak adanya aplikasi HCIS', 'Berdampak kepada seluruh unit kerja', 'Data-data yang akan diinput masih belum matang/belum final/belum disahkan sejak tahun 2015', 'Harus dibuat tim lagi untuk membuat WI atau SOP yang belum selesai', 'Kerugian ada pada tidak optimalnya aplikasi HCIS', 'Dilaporkan tanggal 12 Juni 2017', 'pada rapat kinerja Mei 2017', 'ke direksi dan peserta rapat', '<p>Ada beberapa data yang belum dapat dimasukkan ke aplikasi HCIS. PIC dari proses ini adalah Bu Tetty. Terjadi sejak adanya aplikasi HCIS. Berdampak kepada seluruh unit kerja. Penyebabnya ada data-data yang akan diinput masih belum matang/belum final/belum disahkan WI nya seperti Profil Jabatan, Kompetensi Karyawan,Demografi Karyawan. Ada juga aplikasi yang belum sempurna untuk diinput seperti Katalog Kompetensi. Mitigasinya adalah menyelesaikan WI atau SOP yang belum selesai, dan untuk sistem yang belum selesai direncanakan untuk mengontrak P Triardi. Kerugian ada pada tidak optimalnya aplikasi HCIS. Dilaporkan tanggal 26 Juli 2017 pada rapat kinerja bulanan Juni 2017 ke direksi dan peserta rapat.</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000155', '51', 'M4100', '6', '2017', '<p>Progress 4,23%</p>\r\n', 'Banyak data yang tidak diinput oleh PIC nya', 'Pelatihan dikoordinir oleh Bu Dian', 'Sejak GCA 2017 diberlakukan', 'Berdampak kepada semua unit kerja', 'Untuk pelatihan eksternal anggaran ditiadakan, sementara pelatihan internal saat ini diserahkan kepada masing-masing unit kerja tetapi tidak diinput di GCA', 'Masing-masing ketua unit kerja mengisi pelatihan internalnya ', 'Kerugian berupa minimnya kesempatan untuk mengembangkan keahlian dari karyawan', 'Dilaporkan tanggal 12 Juni 2017', 'pada rapat kinerja Mei 2017', 'ke Direksi dan peserta rapat', '<p>Banyak data yang tidak diinput oleh PIC nya di GCA. Pelatihan dikoordinir oleh Bu Dian. Hal ini berdampak kepada semua unit kerja. Tahun 2017 untuk pelatihan eksternal anggaran ditiadakan, sementara pelatihan internal saat ini diserahkan kepada masing-masing unit kerja tetapi tidak diinput di GCA. Untuk progress pencapaian targetnya diminta masing-masing ketua unit kerja mengisi pelatihan internalnya. Kerugian berupa minimnya kesempatan untuk mengembangkan keahlian dari karyawan. Dilaporkan tanggal 26 Juli 2017 pada rapat kinerja Juni 2017 ke Direksi dan peserta rapat.</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000155', '99', 'M1002', '6', '2017', '<p>Proses perpanjangan domisili Perusahaan belum selesai, karena ada perubahan permohonan manual ke online.</p>\r\n', 'Menyiapkan softcopy Dokumen (scan) pendukung untuk diupload.\r\n\r\nKontrak sewa gedung akan berakhir', 'Scan dokumen (softcopy) pendukung = Novi\r\nKontrak sewa gedung KS = Yuliarso ', 'Tanggal 17 Juli sampai dengan tanggal 31 Juli 2017', 'SBU', 'Karena ada perubahan permohonan manual ke online', 'Menyiapkan dokumen  pendukung sebelumnya', 'Tidak ada', 'Rapat kinerja', 'Ruang Rapat KC', 'Dirut', '<p>Menyiapkan softcopy Dokumen (scan) pendukung untuk diupload. Kontrak sewa gedung akan berakhir. Scan dokumen (softcopy) pendukung = Novi Kontrak sewa gedung KS = Yuliarso . Tanggal 17 Juli sampai dengan tanggal 31 Juli 2017. SBU. Karena ada perubahan permohonan manual ke online. Menyiapkan dokumen pendukung sebelumnya. Tidak ada. Rapat kinerja. Ruang Rapat KC. Dirut</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000156', '49', 'M4100', '6', '2017', '', 'Progress 53,80%', 'Seluruh personil HC bertanggung jawab mengisi progress ini', 'Sejak berlakunya GCA 2017', 'Yang berdampak adalah kinerja HC', 'Ada target-target yang tidak terealisasi seperti Kinerja Mingguan, pembahasan SK-SK yang berkaitan dengan PKB. Ada juga yang terealisasi tetapi lupa mengisinya.', 'Mitigasinya adalah disiplin dalam mengisi progress pekerjaan tambahan, termasuk pekerjaan yang tidak direncanakan sebelumnya seperti menghadiri undangan, rapat yang tidak terjadual, atau menjadi assessor KPKU', 'Kerugian dari kurang baiknya prosentase kinerja HC untuk pekerjaan-pekerjaan tambahan', 'Dilaporkan tanggal 26 Juli 2017', 'Pada rapat Kinerja Bulanan Perusahaan Juni 2017', 'Ke Direksi dan peserta rapat', '<p>Progress 53,80%. Seluruh personil HC bertanggung jawab mengisi progress ini. Input progress ini berlaku sejak adanya GCA 2017. Yang berdampak adalah kinerja HC. Ada target-target yang tidak terealisasi seperti Kinerja Mingguan, pembahasan SK-SK yang berkaitan dengan PKB. Ada juga yang terealisasi tetapi lupa mengisinya.. Mitigasinya adalah disiplin dalam mengisi progress pekerjaan tambahan, termasuk pekerjaan yang tidak direncanakan sebelumnya seperti menghadiri undangan, rapat yang tidak terjadual, atau menjadi assessor KPKU. Kerugian dari progress ini adalah kurang baiknya prosentase kinerja HC untuk pekerjaan-pekerjaan tambahan. Dilaporkan tanggal 26 Juli 2017 pada rapat Kinerja Bulanan Perusahaan Juni 2017 ke Direksi dan peserta rapat.</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000157', '42', 'M4100', '6', '2017', '', 'Progress 73,73%', 'PIC dari proses ini adalah Bu Tetty', 'Proses ini dilakukan sejak berlakunya GCA 2017', 'Berdampak pada tidak tercapainya progress payroll yang sebenarnya telah dilakukan semua', 'Hal ini terjadi karena ada beberapa item pekerjaan yang tidak diisi karena sudah terwakili oleh item pekerjaan lain yang mirip.', 'Mitigasinya adalah menghilangkan item-item pekerjaan yang tidak perlu dilakukan atau mirip dengan pekerjaan yang lain', 'Kerugiannya ada pada kinerja Divisi HC', 'Dilaporkan pada 26 Juli 2017', 'Pada Rapat Kinerja Bulanan Perusahaan Juni 2017', 'Ke Direksi dan peserta rapat', '<p>Progress 73,73%. PIC dari proses ini adalah Bu Tetty. Proses ini dilakukan sejak berlakunya GCA 2017. Berdampak pada tidak tercapainya progress payroll yang sebenarnya telah dilakukan semua. Hal ini terjadi karena ada beberapa item pekerjaan yang tidak diisi karena sudah terwakili oleh item pekerjaan lain yang mirip.. Mitigasinya adalah menghilangkan item-item pekerjaan yang tidak perlu dilakukan atau mirip dengan pekerjaan yang lain. Kerugiannya ada pada kinerja Divisi HC. Dilaporkan pada 26 Juli 2017 pada Rapat Kinerja Bulanan Perusahaan Juni 2017 ke Direksi dan peserta rapat.</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000157', '110', 'KL', '6', '2017', '<p>SBU E/I</p>\r\n', 'Pencapaian target margin proyek masih 0 dari seharusnya 18%', ' Mgr SBU', 'Sejak 1 Juni 2017 s/d 30 Juni 2017', 'SBU EI', 'Data RAB sudah ada di Simpro (cc proyek KL21701), ada masalah dg aplikasi Simpro', 'Koordinasi dengan Divisi Keuangan & programmer Simpro', 'Pencapaian target margin proyek masih 0 (performance SBU kurang baik)', 'Akan dilaporkan pada tgl. 24 Juli 2017', 'Dalam rapat Review Kinerja & SKK', 'Direksi', '<p>Pencapaian target margin proyek masih 0 dari seharusnya 18%. Mgr SBU. Sejak 1 Juni 2017 s/d 30 Juni 2017. SBU EI. Data RAB sudah ada di Simpro (cc proyek KL21701), ada masalah dg aplikasi Simpro. Koordinasi dengan Divisi Keuangan &amp; programmer Simpro. Pencapaian target margin proyek masih 0 (performance SBU kurang baik). Akan dilaporkan pada tgl. 24 Juli 2017. Dalam rapat Review Kinerja &amp; SKK. Direksi</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000158', '111', 'KL', '6', '2017', '<p>&nbsp;Proyek Paket E&amp;I New Pipeline Extension from BF to HSM1 &amp; CRM&nbsp;</p>\r\n', 'Pencapaian target pendapatan proyek masih 0.24 dari seharusnya 4.7 M', 'Mgr SBU', 'Sejak 1 Juni 2017 s/d 30 Juni 2017', 'SBU EI', 'Tahun lalu tidak mendapat proyek di KSI. Proyek tambahan baru yang didapat nilainya belum mencukupi', 'Mencari proyek / pengadaan baru di PT KS', 'Pencapaian pendapatan proyek masih Rp. 0.24 M', 'Akan dilaporkan pada tgl. 24 Juli 2017', 'Dalam rapat Review Kinerja & SKK', 'Direksi', '<p>Pencapaian target pendapatan proyek&nbsp;Paket E&amp;I New Pipeline Extension from BF to HSM1 &amp; CRM masih untuk Uang Muka sebesar Rp 253 Juta</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000159', '112', 'KL', '6', '2017', '', 'Belum didapatkannya project baru sehingga mempengaruhi target kontrak baru', 'Manager SBU', 'Sejak 1 Juni 2017 s/d 30 Juni 2017', 'SBU EI', 'Tidak di dapatkannya project baru', 'Mendapatkan kontrak baru dari PT KS', 'Target kontrak bulan Juni tidak tercapai', 'Akan dilaporkan pada tgl. 24 Juli 2017', 'Dalam rapat Review Kinerja & SKK', 'Direksi', '<p>Belum didapatkannya project baru sehingga mempengaruhi target kontrak baru. Manager SBU. Sejak 1 Juni 2017 s/d 30 Juni 2017. SBU EI. Tidak di dapatkannya project baru. Mendapatkan kontrak baru dari PT KS. Target kontrak bulan Juni tidak tercapai. Akan dilaporkan pada tgl. 24 Juli 2017. Dalam rapat Review Kinerja &amp; SKK. Direksi</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000160', '117', 'KL', '6', '2017', '', 'Pencapaian target penyelesaian kegiatan sales masih 21.74 % dari 100%', 'SBU-E&I', 'Sejak 1 Juni 2017 s/d 30 Juni 2017', 'Semua personil SBU EI', 'Tidak semua personil mengisi progress GCA dan pengisian tidak mengikuti rencana di GCA', 'Menambahkan program/rencana kerja sesuai peluang/prospek yg sedang ditangani', 'Pencapaian progress bulan Juni hanya 21.74 %, tidak tercapai target penyelesaian sales', 'Akan dilaporkan pada tgl. 24 Juli 2017', 'Dalam rapat Review Kinerja & SKK', 'Direksi', '<p>Pencapaian target penyelesaian kegiatan sales masih 21.74 % dari 100%. SBU-E&amp;I. Sejak 1 Juni 2017 s/d 30 Juni 2017. Semua personil SBU EI. Tidak semua personil mengisi progress GCA dan pengisian tidak mengikuti rencana di GCA. Menambahkan program/rencana kerja sesuai peluang/prospek yg sedang ditangani. Pencapaian progress bulan Juni hanya 21.74 %, tidak tercapai target penyelesaian sales. Akan dilaporkan pada tgl. 24 Juli 2017. Dalam rapat Review Kinerja &amp; SKK. Direksi</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000161', '119', 'KL', '6', '2017', '', 'Pencapaian target program kerja tambahan hanya 25.53% dari seharusnya 100%', 'Manager SBU', 'Sejak 1 Juni 2017 s/d 31 Juni 2017', ' SBU EI', 'Ada perubahan penanggung jawab program kerja', 'Revisi program kerja', 'Pencapaian progress tidak 100%', 'Akan dilaporkan pada tgl. 24 Juli 2017', 'Dalam rapat Review Kinerja & SKK', 'Direksi', '<p>Pencapaian target program kerja tambahan hanya 25.53% dari seharusnya 100%. Manager SBU. Sejak 1 Juni 2017 s/d 31 Juni 2017. SBU EI. Ada perubahan penanggung jawab program kerja. Revisi program kerja. Pencapaian progress tidak 100%. Akan dilaporkan pada tgl. 24 Juli 2017. Dalam rapat Review Kinerja &amp; SKK. Direksi</p>\r\n', null, null, null);
