INSERT INTO `ket_progress_srko` VALUES ('17000164', '122', 'KL', '6', '2017', '', 'Pencapaian target jam pelatihan masih 0 jam dari seharusnya 6 jam', 'Semua personil SBU EI', 'Sejak 1 Juni 2017 s/d 30 Juni 2017', 'SBU EI', 'Mengejar penyelesaian proyek', 'Penyusunan target pelatihan agar disesuaikan dengan aktifitas SBU pada bulan berjalan dan meminta Divisi HC menggalakkan training internal', 'Pencapaian target pelatihan tertinggal lagi 6 jam', 'Akan dilaporkan pada tgl. 24 Juli 2017', 'Dalam rapat Review Kinerja & SKK', 'Direksi', '<p>Pencapaian target jam pelatihan masih 0 jam dari seharusnya 6 jam. Semua personil SBU EI. Sejak 1 Juni 2017 s/d 30 Juni 2017. SBU EI. Mengejar penyelesaian proyek. Penyusunan target pelatihan agar disesuaikan dengan aktifitas SBU pada bulan berjalan dan meminta Divisi HC menggalakkan training internal. Pencapaian target pelatihan tertinggal lagi 6 jam. Akan dilaporkan pada tgl. 24 Juli 2017. Dalam rapat Review Kinerja &amp; SKK. Direksi</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000165', '121', 'KL', '6', '2017', '', 'Pencapaian target peningkatan produktivitas 0 %', 'Manager SBU', 'Sejak 1 Juni 2017 s/d 30 Juni 2017', 'SBU EI', 'Tagihan progress proyek COG HSM belum diproses PTKE', 'Meminta project control PTKE untuk segera menghitung/menyepakati pencapaian progress', 'Pencapaian progress tidak 100%', 'Akan dilaporkan pada tgl. 24 Juli 2017', 'Dalam rapat Review Kinerja & SKK', 'Direksi', '<p>Pencapaian target peningkatan produktivitas 0 %. Manager SBU. Sejak 1 Juni 2017 s/d 30 Juni 2017. SBU EI. Tagihan progress proyek COG HSM belum diproses PTKE. Meminta project control PTKE untuk segera menghitung/menyepakati pencapaian progress. Pencapaian progress tidak 100%. Akan dilaporkan pada tgl. 24 Juli 2017. Dalam rapat Review Kinerja &amp; SKK. Direksi</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000166', '89', 'KE', '6', '2017', '<p>Terjadi Keterlambatan Input data di SSO. Keterlambatan terjadi sejak Cuti Lebaran sampai penutupa...</p>\r\n', 'Keterlambatan Input di SSO', 'Subandi', 'Sejak Cuti Lebaran', 'Logistik', 'Lupa Input di SSO', 'Mencatat rencana kerja di buku', 'Target  Pencapaian bulanan menjadi 0%', 'Tanggal 26 Juli 2017 pada saat rapat kinerja bulanan', 'Di ruang rapat KC', 'Ke Direksi PT.KIT', '<p>Terjadi Keterlambatan Input data di SSO. Keterlambatan terjadi sejak Cuti Lebaran sampai penutupan input SSO bulanan, karena lupa. Sehingga ini berdampak pada kinerja unit kerja Logistik. Keterlambatan ini akan dilaporkan pada tanggal 26 juli 2017 kepada Direksi di ruang rapat KC. Akhirnya masalah ini akan diselesaikan dengan cara mencatat rencana kerja di buku.</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000167', '152', 'KJ', '6', '2017', '<p>Proyek Pengembangan SIKTI dan Proses PT Krakatau Tirta Industri -&nbsp;BA Serah Terima Dokumentasi dan Training Rp. 475,000,000 masih menunggu pemasangan Running Text Yang rusak.</p>\r\n\r\n<p>BA &amp; LHP&nbsp;BA Masa Pemeliharaan&nbsp;Rp.&nbsp;190,000,000.00 &nbsp; Bulan Oktober 2017&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '-', '-', '-', '-', '-', '-', '-', '-', '-', '-', '', null, null, null);
