INSERT INTO `ket_progress_srko` VALUES ('17000319', '164', 'KD', '8', '2017', '', 'Target Margin sebesar 17% tidak tercapai', 'SBU Infrastruktur', 'Terjadi pada Kinerja Agustus 2017', 'Berdampak pada Pendapatan Perusahaan', 'Dikarenakan tagihan Proyek KNSS milestone 6 terlambat menyelesaikan laporan dengan detail format yang diminta user dan juga disebabkan oleh pengeluaran perusahaan bulan Agustus yang besar', 'Diatasi dengan mempercepat penyelesaian laporan proyek ', 'Terjadi kerugian pendapatan yang seharusnya masuk sekitar 2 M', 'Dilaporkan pada 18 September 2017', 'pada Rapat Review Kinerja', 'Yang dihadiri oleh BOD dan peserta Rapat', '<p>Target Margin sebesar 17% tidak tercapai. SBU Infrastruktur. Terjadi pada Kinerja Agustus 2017. Berdampak pada Margin Perusahaan. Dikarenakan penangguhan biaya proyek dibebankan pada bulan ini khususnya proyek KNSS. Dilaporkan pada 18 September 2017. pada Rapat Review Kinerja. Yang dihadiri oleh BOD dan peserta Rapat</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000321', '165', 'KD', '8', '2017', '', 'Target Meraih Pendapatn Proyek bulan Agstus tidak tercapai. ', 'Terkait pada penyelesaian laporan akhir proyek KNSS dengan detail sesuai permintaan user', 'Terjadi sejak Juli 2017', 'Berdampak pada area SBU Infrasruktur', 'Format Laporan Proyek harus mengikuti model laporan  user (Jepang), sehingga banyak revisi yang harus dilakukan dan membutuhkan resources yang cukup banyak', 'Diatasi dengan benyak melakukan pendekatan dan belajar pada user, dan meminta bantuan pada user', 'Kerugian financial sekitar 2 M karena keterlambatan BA penagian', 'Dilaporkan pada 18 September 2017', 'Pada Rapat Review Kinerja Bulanan', 'yang dihadiri oleh BOD dan peserta rapat', '<p>Target Meraih Pendapatn Proyek bulan Agstus tidak tercapai. . Terkait pada penyelesaian laporan akhir proyek KNSS dengan detail sesuai permintaan user. Terjadi sejak Juli 2017. Berdampak pada area SBU Infrasruktur. Format Laporan Proyek harus mengikuti model laporan user (Jepang), sehingga banyak revisi yang harus dilakukan dan membutuhkan resources yang cukup banyak. Diatasi dengan benyak melakukan pendekatan dan belajar pada user, dan meminta bantuan pada user. Kerugian financial sekitar 2 M karena keterlambatan BA penagian. Dilaporkan pada 18 Agustus 2017. Pada Rapat Review Kinerja Bulanan. yang dihadiri oleh BOD dan peserta rapat</p>\r\n', null, null, null);
