INSERT INTO `ket_progress_srko` VALUES ('17000322', '167', 'KD', '8', '2017', '', 'Target meraih pendapatan Repeat Order bulan Agustus tidak tercapai', 'Terkait dengan SBU Infrastruktur', 'Terjadi pada bulan Agustus 2017', 'Berdampak pada SBU Infrastruktur', 'Target yang direncanakan pada bulan Agustus tidak diperoleh, namun secara keseluruhan sampai bulan ini target tercapai', 'tidak ada cara menghindari karena perolehan bergeser pada bulan sebelumnya', 'Tidak ada kerugian financial', 'Dilaporkan pada 18 September 2017', 'pada Rapat Review Kinerja Bulanan', 'Yang dihadiri oleh BOD dan peserta Rapat', '<p>Target meraih pendapatan Repeat Order bulan Agustus tidak tercapai. Terkait dengan SBU Infrastruktur. Terjadi pada bulan Agustus 2017. Berdampak pada SBU Infrastruktur. Target yang direncanakan pada bulan Agustus tidak diperoleh, namun secara keseluruhan sampai bulan ini target tercapai. tidak ada cara menghindari karena perolehan bergeser pada bulan sebelumnya. Tidak ada kerugian financial. Dilaporkan pada 18 Agustus 2017. pada Rapat Review Kinerja Bulanan. Yang dihadiri oleh BOD dan peserta Rapat</p>\r\n', null, null, null);
