INSERT INTO `ket_progress_srko` VALUES ('17000323', '171', 'KD', '8', '2017', '', 'Target pelaksanaan penyelesaikan sales belum tercapai', 'Terkait dengan para Account Manager', 'Terjadi sejak buan Januari', 'Berdampak pada SBU Infrastruktur', 'Secara pekerjaan penyelesaian Sales sudah dilakukan dengan baik, hanya kedisiplinan menyelesaikan GCA yang belum dilakukan secara keselruhan sehingga perencanaan di GCA belum secara keseluruhan ditindaklanjuti.', 'Akan diatasi dengan menyelesaikan GCA-GCA yang sudah direncanakan', 'Tidak ada kerugian financial', 'Dilaporkan pada 18 September 2017', 'Pada Rapat Review Kinerja Bulanan yang dihadiri oleh BOD dan peserta Rapat', '.', '<p>Target pelaksanaan penyelesaikan sales belum tercapai. Terkait dengan para Account Manager. Terjadi sejak buan Januari. Berdampak pada SBU Infrastruktur. Secara pekerjaan penyelesaian Sales sudah dilakukan dengan baik, hanya kedisiplinan menyelesaikan GCA yang belum dilakukan secara keselruhan sehingga perencanaan di GCA belum secara keseluruhan ditindaklanjuti.. Akan diatasi dengan menyelesaikan GCA-GCA yang sudah direncanakan. Tidak ada kerugian financial. Dilaporkan pada 18 Agustus 2017. Pada Rapat Review Kinerja Bulanan yang dihadiri oleh BOD dan peserta Rapat. .</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000324', '173', 'KD', '8', '2017', '', 'Program kerja tambahan beljm memenuhi target', 'Team pak Bambang Nugroho', 'Sejak Januari 2017', 'Berdampak pada area Kantor PT Krakatau IT', 'Terjadi karena beberapa program belum berjalan sesuai rencana', 'Diatasi dengan mempercepat keterlambatan penyelesIan program yang ditetapkan dan mengatasi kendala-kendala yang muncul', 'Tidak ada kerugian finansial', 'Dilaporkan pada 18 September 2017', 'Pada Rapat Review Kinerja', 'Yang dihadiri oleh BOD dan Peserta Rapat', '<p>Program kerja tambahan beljm memenuhi target. Team pak Bambang Nugroho. Sejak Januari 2017. Berdampak pada area Kantor PT Krakatau IT. Terjadi karena beberapa program belum berjalan sesuai rencana. Diatasi dengan mempercepat keterlambatan penyelesIan program yang ditetapkan dan mengatasi kendala-kendala yang muncul. Tidak ada kerugian finansial. Dilaporkan pada 21 Agustus 2017. Pada Rapat Review Kinerja. Yang dihadiri oleh BOD dan Peserta Rapat</p>\r\n', null, null, null);
