INSERT INTO `ket_progress_srko` VALUES ('17000326', '176', 'KD', '8', '2017', '', 'Target meningkatkan produktivitas bulan ini tidak tercapai', 'Terkait pada keseluruhan SBu Infrastruktur', 'Terjadi sejak Juli 2017', 'Berdampak pada SBU Infrastruktur', 'Disebabkan karenan target pendapatan yang dijanjikan tidak tercapai dan terjadi ketelambatan sehingga produktivitas karyawan turun', 'Diatasi dengan mempercepat penagihan piutang-piutang yang tertunda', 'Kerugian financial sebesar cost of money pada bulan tersebut', 'Dilaporkan pada 18 September 2017 pada Rapat Review Kinerja yang dihadiri oleh BOD san peserta Rapat', '.', '.', '<p>Target meningkatkan produktivitas bulan ini tidak tercapai. Terkait pada keseluruhan SBu Infrastruktur. Terjadi sejak Juli 2017. Berdampak pada SBU Infrastruktur. Disebabkan karenan target pendapatan yang dijanjikan tidak tercapai dan terjadi ketelambatan sehingga produktivitas karyawan turun. Diatasi dengan mempercepat penagihan piutang-piutang yang tertunda. Kerugian financial sebesar cost of money pada bulan tersebut. Dilaporkan pada 18 September 2017 pada Rapat Review Kinerja yang dihadiri oleh BOD san peserta Rapat. .. .</p>\r\n', null, null, null);
