INSERT INTO `ket_progress_srko` VALUES ('17000328', '177', 'KD', '8', '2017', '', 'Taarget jam pelatihan Karyawan belum tercapai', 'Terkait dengan seluruh personil SBU Infrastruktur', 'Terjadi sejak Januari 2017', 'Berdampak pada SBU Ifrastruktur', 'Hal tersebut disebabkan adanya kendala waktu yang diluangkan oleh para personil, terutama yang terlibat dalam proyek, karena kebanyak waktu tersita untuk penyelesaian proyek', 'Diatasi dengan cara pembelajaran mandiri, hal inipun untuk persnonil proyek masih belum bisa dilakukan secara baik', 'Tidak mengakibatkan kerugian financial secara langsung, cuma akan menghambat kemanjuan personil kedepanya', 'Dilaporkan pada 18 September 2017 pada Rapat Review Kinerja Bulanan yang dihadiri oleh BOD dan Peserta Rapat', '.', '.', '<p>Taarget jam pelatihan Karyawan belum tercapai. Terkait dengan seluruh personil SBU Infrastruktur. Terjadi sejak Januari 2017. Berdampak pada SBU Ifrastruktur. Hal tersebut disebabkan adanya kendala waktu yang diluangkan oleh para personil, terutama yang terlibat dalam proyek, karena kebanyak waktu tersita untuk penyelesaian proyek. Diatasi dengan cara pembelajaran mandiri, hal inipun untuk persnonil proyek masih belum bisa dilakukan secara baik. Tidak mengakibatkan kerugian financial secara langsung, cuma akan menghambat kemanjuan personil kedepanya. Dilaporkan pada 18 September 2017 pada Rapat Review Kinerja Bulanan yang dihadiri oleh BOD dan Peserta Rapat.</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000329', '37', 'M4100', '8', '2017', '', 'Realisasi 0%', 'PIC dari progress ini adalah Bu Ida dan Bu Rika', 'Terjadi sejak diberlakukan GCA 2017', 'Tidak ada yang berdampak', 'Katalog Kompetensi sudah selesai dan belum ada rencana untuk diupdate pada tahun ini', 'Mitigasi dengan membatasi waktu pelaksanaan menjadi 0 hari', 'Tidak ada kerugian akibat hal ini', 'Dilaporkan tanggal 18 September 2017', 'Pada rapat kinerja bulanan ', 'Ke direksi dan peserta rapat', '<p>Realisasi 0%. PIC dari progress ini adalah Bu Ida dan Bu Rika. Terjadi sejak diberlakukan GCA 2017. Tidak ada yang berdampak karena katalog kompetensi sudah selesai dan belum ada rencana untuk diupdate pada tahun ini. Mitigasi agar prosentase 0% dengan membatasi waktu pelaksanaan menjadi 0 hari. Tidak ada kerugian akibat hal ini. Dilaporkan tanggal 18 September 2017. Pada rapat kinerja bulanan Agustus 2017&nbsp; ke direksi dan peserta rapat.</p>\r\n', null, null, null);
INSERT INTO `ket_progress_srko` VALUES ('17000330', '87', 'KE', '8', '2017', '<p>Target baru mencapai 96,67% dari seharusnya 100% karena belum memasukkan aktivitas penerimaan bar...</p>\r\n', 'Target baru mencapai 96,67% dari seharusnya 100% karena belum memasukkan aktivitas penerimaan barang', 'Agus Caturi', 'Sejak 1 agustus 2017 s/d 31 agustus 2017', 'Logistik', 'Belum menginput aktivitas penerimaan barang', 'aktivitas bulan tersebut di nol kan dulu', 'target kurang 3.33%', 'Tanggal 18 September 2017', 'Rapat Kinerja', 'Ke Direksi', '<p>Target baru mencapai 96,67% dari seharusnya 100% karena belum memasukkan aktivitas penerimaan barang. Agus Caturi. Sejak 1 agustus 2017 s/d 31 agustus 2017. Logistik. Belum menginput aktivitas penerimaan barang. aktivitas bulan tersebut di nol kan dulu. target kurang 3.33%. Tanggal 18 September 2017. Rapat Kinerja. Ke Direksi</p>\r\n', null, null, null);
