INSERT INTO `ket_progress_srko` VALUES ('17000448', '156', 'KJ', '11', '2017', '<p>Penyelesaian Proyek Sesuai Skedul</p>\r\n', 'Hambatan Penyelesaian Proyek Sesuai Skedul diantaranya dari sistem Pengadaan Barang yang perlu dibakukan sistemnya untuk mencari harga termurah dan sistem pembayaran yang menguntungkan dari PIHAK KIT dan Pihak Pemasok. Penyelesian Proyek yang terkendala  adalah Proyek COG KE, SCADA KTI, dan Inovasi CRM.', '.', '.', '.', '.', '.', '.', '.', '.', '.', '<p>Hambatan Penyelesaian Proyek Sesuai Skedul diantaranya dari sistem Pengadaan Barang yang perlu dibakukan sistemnya untuk mencari harga termurah dan sistem pembayaran yang menguntungkan dari PIHAK KIT dan Pihak Pemasok. Penyelesian Proyek yang terkendala adalah Proyek COG KE, SCADA KTI, dan Inovasi CRM.. .. .. .. .. .. .. .. .. .</p>\r\n', null, null, null);
