INSERT INTO `ket_progress_srko` VALUES ('18000500', '272', 'M4200', '1', '2018', '<p>Review dan Pelaporan kinerja</p>\r\n', ',', ',', ',', ',', ',', ',', ',', ',', ',', ',', '<p>Review dan pelaporan kinerja tidak terampil di KKWK.</p>\r\n', null, null, null);
